mapper.select:
select * from ( select rownum as RNUM, LIST_ALL.* from (select free_no, title, content, group_no, userid, nickname, regdate, read_count, (sysdate-regdate)*24 as newImgTerm, step, sort_no, del_yn from free_board
order by group_no desc, sort_no asc) LIST_ALL )
where RNUM> #{firstRecordIndex} and RNUM<= #{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update free_board 
set read_count=read_count+1
where free_no=#{freeNo}

update free_board 
	set title=#{title}, content=#{content}
where free_no=#{freeNo}

update free_board
	set sort_no=sort_no+1
where group_no=#{groupNo} and sort_no > #{sortNo}
